from solana.publickey import PublicKey

import base64

import mango.layouts as layouts


def test_group_layout():
    encoded_group = ""
    decoded_group = base64.b64decode(encoded_group)

    group = layouts.GROUP.parse(decoded_group)

    # Not an exhaustive check, just a few key areas
    assert group.num_oracles == 1
    assert len(group.tokens) == 32
    assert group.tokens[0].mint == PublicKey("bypQzRBaSDWiKhoAw3hNkf35eF3z3AZCU8Sxks6mTPP")
    assert group.tokens[1].mint is None
    assert group.tokens[31].mint == PublicKey("EMjjdsqERN4wJUR9jMBax2pzqQPeGLNn5NeucbHpDUZK")

    assert len(group.spot_markets) == 31
    assert group.spot_markets[0].spot_market == PublicKey("E1mfsnnCcL24JcDQxr7F2BpWjkyy5x2WHys8EL2pnCj9")
    assert group.spot_markets[1].spot_market is None
    assert group.spot_markets[30].spot_market is None

    assert len(group.perp_markets) == 31
    assert group.perp_markets[0].perp_market == PublicKey("2qCnz8qWu3mSouLFxNMDu2BuCHPVCgB5XLjASFroenyD")
    assert group.perp_markets[1].perp_market is None
    assert group.perp_markets[30].perp_market is None

    assert len(group.oracles) == 31
    assert group.oracles[0] == PublicKey("EFAo16g6vKBjNgfRhsz1C4w5x9TW6hyQcSf3pKKGxf5x")
    assert group.oracles[1] is None
    assert group.oracles[30] is None

    assert group.signer_nonce == 5
    assert group.signer_key == PublicKey("De1uz6aUzESsoDSxgoSwwwsdWfciMkiAfPpomkQaJJpE")
    assert group.admin == PublicKey("Cwg1f6m4m3DGwMEbmsbAfDtUToUf5jRdKrJSGD7GfZCB")
    assert group.dex_program_id == PublicKey("DESVgJVGajEgKGXhb6XmqDHGz3VjdgP7rEVESBgxmroY")
    assert group.cache == PublicKey("EVBrBQydVjTe13jCZABs7fsUt9TnvbDSPiPCm4tSzio7")
    assert group.dao_vault == PublicKey("4WyS3cxA69kMPBbD7isScdTqzxYkMvSL6gisqw9E3id5")
    assert group.valid_interval == 5
